mapper.select:
select 

from atb_rate
where id = #{id,jdbcType=INTEGER}
  and process = #{process,jdbcType=INTEGER}

mapper.delete:
delete from atb_rate
where id = #{id,jdbcType=INTEGER}
  and process = #{process,jdbcType=INTEGER}

mapper.update:
update atb_rate


    date = #{date,jdbcType=TIME},


    produce = #{produce,jdbcType=INTEGER},


where id = #{id,jdbcType=INTEGER}
  and process = #{process,jdbcType=INTEGER}

update atb_rate
set date = #{date,jdbcType=TIME},
  produce = #{produce,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
  and process = #{process,jdbcType=INTEGER}
